import React, {Component} from 'react';
import './App.css';
import "bootstrap/dist/css/bootstrap.css";
import { Navbar, NavItem, Nav, Container, Row, Col, MenuItem, NavDropdown} from "react-bootstrap";


const PLACES = [
    {name: "Харьков", city: "Kharkov"},
    {name: "Свесса", city: "Svesa"},
    {name: "Сумы", city: "Sumy"},
    {name: "Киев", city: "Kyiv"}
];

class WeatherDisplay extends Component {
    constructor() {
        super();
        this.state = {
            weatherData: null
        };
    }

    componentDidMount() {
        const city = this.props.city;
        const URL = "http://api.openweathermap.org/data/2.5/weather?q=" +
            city +
            "&appid=789d8bf96c25b83d0b02c6bb6f750d34&units=metric";
        fetch(URL).then(res => res.json()).then(json => {
            this.setState({weatherData: json});
        });
    }

    render() {
        const weatherData = this.state.weatherData;
        if (!weatherData) return <div>Loading</div>;
        const weather = weatherData.weather[0];
        const iconUrl = "http://openweathermap.org/img/w/" + weather.icon + ".png";
        return (
            <div>
                <h1>
                    {weather.main} in {weatherData.name}
                    <img src={iconUrl} alt={weatherData.description}/>
                </h1>
                <p>Current: {(weatherData.main.temp).toFixed(0)}°С</p>
                <p>High: {(weatherData.main.temp_max).toFixed(0)}°С</p>
                <p>Low: {(weatherData.main.temp_min).toFixed(0)}°С</p>
                <p>Wind Speed: {(weatherData.wind.speed)} m/s</p>
            </div>
        )
    }
}

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            activePlace: 0,
        };
    }

    render() {
        const activePlace = this.state.activePlace;

        return (
            <div>
                <Navbar>
                    <Navbar.Header>
                        <Navbar.Brand>
                            <a href="#home">React-Bootstrap</a>
                        </Navbar.Brand>
                    </Navbar.Header>
                    <Nav>
                        <NavItem eventKey={1} href="#">
                            Link
                        </NavItem>
                        <NavItem eventKey={2} href="#">
                            Link
                        </NavItem>
                        <NavDropdown eventKey={3} title="Dropdown" id="basic-nav-dropdown">
                            <MenuItem eventKey={3.1}>Action</MenuItem>
                            <MenuItem eventKey={3.2}>Another action</MenuItem>
                            <MenuItem eventKey={3.3}>Something else here</MenuItem>
                            <MenuItem divider />
                            <MenuItem eventKey={3.4}>Separated link</MenuItem>
                        </NavDropdown>
                    </Nav>
                </Navbar>;
                <Container>
                    <Row>
                        <Col md={4} sm={4}>
                            <h3>Select a city</h3>
                            <Nav
                                className="flex-sm-column"
                                variant="pills"
                                activeKey={activePlace}
                                onSelect={index => {
                                    this.setState({activePlace: index});
                                }}
                            >
                                {PLACES.map((place, index) => (
                                    <NavItem>
                                        <Nav.Link key={index} eventKey={index}>{place.name}</Nav.Link>
                                    </NavItem>
                                ))}
                            </Nav>
                        </Col>
                        <Col md={8} sm={8}>
                            <WeatherDisplay key={activePlace} city={PLACES[activePlace].city}/>
                        </Col>
                    </Row>
                </Container>
            </div>
        );
    }
}

export default App;
